$(document).ready(function () {
//######################################################################################################
//                              Load modules statics
//######################################################################################################

    function staticHome() {
        $('#webminContentTop').load('./components/top.html');
        $('#webminContentMain').load('./components/main.html');
        $('#webminContentLateral').load('./components/lateral.html');
        $('#webminContentFooter').load('./components/footer.html');
    }
//######################################################################################################
//                              Start History.js framework
//######################################################################################################
    
    History.Adapter.bind(window, 'statechange', function () {
        var State = History.getState();
        //console.log('statechange:', State.data, State.title, State.url);
    });

//######################################################################################################
 //                             execute Route Root
 //######################################################################################################

    History.pushState({ pagina: 1, url: staticHome() }, "Ratts - Framework ", "/");
    
});
//######################################################################################################
//                              Routes and Functions by Ratts
//######################################################################################################

function main(){
    $('#webminContentMain').load('./components/main.html');
}

function login() {
    $('#webminContentMain').load('./public/loginExample.html');
}

function private(){
    $('#webminContentMain').load('./private/privateExample.html');
}

function mainRout() {
    History.pushState({ pagina: 1, url: main() }, "Ratts - Framework ", "/");
}

function loginRout() {
    History.pushState({ pagina: 2, url: login() }, "Ratts - Framework ", "/login");
}

function privateRout() {
    History.pushState({ pagina: 3, url: private() }, "Ratts - Framework ", "/private");
}


